const INITAL_STATE = {
    currentUser: null
}

const userReducer = (state = INITAL_STATE, action) => { //it takes all the actions
    switch(action.type){
        case 'SET_CURRENT_USER': //if the action match the one the we are looking for in case "SET_CURRENT_USER"
            return{
                ...state,
                currentUser: action.payload //to update the update flexible property.
            };
        
        default:
            return state;
    }
};

export default userReducer;
