import React from 'react';
import '../../redux/shop/shop.data.jsx/index.js';
import {createStructuredSelector} from 'reselect';
// import SHOP_DATA from './shop.data.jsx';
import CollectionPreview from '../../Component/collection-perview/collection-perview.component'
import {selectCollections} from '../../redux/shop/shop.selector'
import {connect} from 'react-redux'; 


const ShopPage = ({collections}) => (  // Ref. To Shop collection
        
        <div className='shop-page'> {collections.map(({id, ...otherCollectionProps}) =>
         ( <CollectionPreview key={id}{...otherCollectionProps}/> ))}
        </div>
);

const mapStateToProps = createStructuredSelector({
    collection: selectCollections
})
    

export default connect(mapStateToProps) (ShopPage);
