//the actual code that combine all the states.
import {combineReducers} from 'redux'; // this combine reducers. it comes in redux
import {persistReducer} from 'redux-persist';
import storage from 'redux-persist/lib/storage';

import userReducer from './user/user.reducer';
import cartReducer from './cart/cart.reducer';
import directoryReducer from './directory/directory.reducer'; 
import shopReducer from './shop/shop.reducer.js';


const persistConfig = {
    key: 'root',
    storage,
    whitelist: ['cart']

}

const rootReducer = combineReducers ({
    user: userReducer,
    cart: cartReducer,
    directory: directoryReducer,
    shop: shopReducer
});

export default persistReducer(persistConfig, rootReducer); // same of the reducer except the with the prisistance capabilities.
